import { Card, CardContent, Typography, Icon } from '@mui/material';

function StatCard({ title, value, icon, color = '#1976d2' }) {
  return (
    <Card 
      sx={{ 
        height: '100%', 
        textAlign: 'center',
        transition: 'transform 0.3s ease-in-out',
        '&:hover': {
          transform: 'translateY(-10px)',
          boxShadow: 6
        }
      }}
    >
      <CardContent>
        <Icon 
          sx={{ 
            fontSize: 48, 
            mb: 2,
            color: color,
            p: 1,
            borderRadius: '50%',
            backgroundColor: `${color}15`
          }}
        >
          {icon}
        </Icon>
        <Typography 
          variant="h3" 
          component="div"
          sx={{ 
            fontWeight: 'bold',
            color: color
          }}
        >
          {value}
        </Typography>
        <Typography 
          variant="h6" 
          color="text.secondary"
          sx={{ mt: 1 }}
        >
          {title}
        </Typography>
      </CardContent>
    </Card>
  );
}

export default StatCard;
