import { useState } from 'react';
import { 
  AppBar, 
  Toolbar, 
  Button, 
  IconButton, 
  Drawer, 
  List, 
  ListItem, 
  ListItemText, 
  Box,
  Container,
  useScrollTrigger,
  Slide
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { useNavigate, useLocation } from 'react-router-dom';
import { Brightness4, Brightness7 } from '@mui/icons-material';

// Scroll olduğunda navbar'ı gizleyen bileşen
function HideOnScroll({ children }) {
  const trigger = useScrollTrigger();
  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {children}
    </Slide>
  );
}

function Navbar({ toggleColorMode, mode }) {
  const navigate = useNavigate();
  const location = useLocation();
  const [mobileOpen, setMobileOpen] = useState(false);

  const menuItems = [
    { text: 'Ana Sayfa', path: '/' },
    { text: 'Hakkımızda', path: '/hakkimizda' },
    { text: 'Projeler', path: '/projeler' },
    { text: 'Galeri', path: '/galeri' },
    { text: 'Sponsorlar', path: '/sponsorlar' },
    // { text: 'Başarılar', path: '/basarilar' },
  ];

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  const drawer = (
    <List>
      {menuItems.map((item) => (
        <ListItem 
          button 
          key={item.text}
          onClick={() => {
            navigate(item.path);
            handleDrawerToggle();
          }}
          sx={{
            backgroundColor: isActive(item.path) ? 'primary.main' : 'transparent',
            color: isActive(item.path) ? 'white' : 'inherit',
            '&:hover': {
              backgroundColor: isActive(item.path) ? 'primary.dark' : 'rgba(0, 0, 0, 0.04)'
            }
          }}
        >
          <ListItemText primary={item.text} />
        </ListItem>
      ))}
      <ListItem button onClick={() => {
        navigate('/katil');
        handleDrawerToggle();
      }}>
        <ListItemText 
          primary="Takıma Katıl" 
          sx={{ 
            color: 'primary.main',
            fontWeight: 'bold'
          }} 
        />
      </ListItem>
    </List>
  );

  return (
    <>
      <HideOnScroll>
        <AppBar 
          position="fixed" 
          sx={{ 
            backgroundColor: mode === 'light' 
              ? 'rgba(255, 255, 255, 0.95)'
              : 'rgba(18, 18, 18, 0.95)',
            backdropFilter: 'blur(8px)'
          }}
        >
          <Container maxWidth="lg">
            <Toolbar 
              sx={{ 
                justifyContent: 'space-between',
                height: 100,
                position: 'relative',
              }}
            >
              {/* Sol taraf - Logo */}
              <Box
                component="img"
                src="/logo.png"
                alt="FEC Logo"
                sx={{ 
                  height: 80,
                  cursor: 'pointer',
                  '&:hover': {
                    opacity: 0.8
                  }
                }}
                onClick={() => navigate('/')}
              />

              {/* Orta - Navigasyon Menüsü */}
              <Box 
                sx={{ 
                  display: { xs: 'none', md: 'flex' }, 
                  alignItems: 'center',
                  gap: 2,
                  position: 'absolute',
                  left: '50%',
                  transform: 'translateX(-50%)'
                }}
              >
                {menuItems.map((item) => (
                  <Button
                    key={item.text}
                    onClick={() => navigate(item.path)}
                    sx={{
                      color: isActive(item.path) ? 'primary.main' : 'text.primary',
                      fontWeight: isActive(item.path) ? 'bold' : 'normal',
                      '&:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.04)'
                      }
                    }}
                  >
                    {item.text}
                  </Button>
                ))}
              </Box>

              {/* Sağ taraf - Tema Değiştirme ve Katıl Butonu */}
              <Box sx={{ 
                display: { xs: 'none', md: 'flex' },
                alignItems: 'center',
                gap: 2
              }}>
                <IconButton 
                  onClick={toggleColorMode} 
                  color={mode === 'light' ? 'inherit' : 'primary'}
                  sx={{ 
                    border: 1,
                    borderColor: 'divider'
                  }}
                >
                  {mode === 'dark' ? (
                    <Brightness7 sx={{ color: 'primary.main' }} />
                  ) : (
                    <Brightness4 />
                  )}
                </IconButton>
                <Button
                  variant="contained"
                  onClick={() => navigate('/katil')}
                >
                  Takıma Katıl
                </Button>
              </Box>

              {/* Mobil menüye de tema değiştirme butonu ekleyelim */}
              <Box sx={{ 
                display: 'flex', 
                alignItems: 'center'
              }}>
                <IconButton 
                  onClick={toggleColorMode} 
                  color={mode === 'light' ? 'inherit' : 'primary'}
                  sx={{ 
                    display: { xs: 'flex', md: 'none' },
                    mr: 1
                  }}
                >
                  {mode === 'dark' ? (
                    <Brightness7 sx={{ color: 'primary.main' }} />
                  ) : (
                    <Brightness4 />
                  )}
                </IconButton>
                <IconButton
                  color="primary"
                  aria-label="open drawer"
                  edge="start"
                  onClick={handleDrawerToggle}
                  sx={{ display: { md: 'none' } }}
                >
                  <MenuIcon />
                </IconButton>
              </Box>
            </Toolbar>
          </Container>
        </AppBar>
      </HideOnScroll>

      {/* Mobile Drawer */}
      <Drawer
        variant="temporary"
        anchor="right"
        open={mobileOpen}
        onClose={handleDrawerToggle}
        ModalProps={{
          keepMounted: true // Daha iyi mobil performansı için
        }}
        sx={{
          display: { xs: 'block', md: 'none' },
          '& .MuiDrawer-paper': { 
            boxSizing: 'border-box', 
            width: 240,
            backgroundColor: 'background.paper'
          },
        }}
      >
        {drawer}
      </Drawer>

      {/* Toolbar'ın yüksekliği kadar boşluk bırakır */}
      <Toolbar />
    </>
  );
}

export default Navbar;
